import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  Image,
  TextInput,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
  Alert,
  ActivityIndicator,
} from "react-native";
import { useRouter } from "expo-router";
import { supabase } from "../supabase/supabaseClient";

export default function Signup() {
  const router = useRouter();
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const validateSignup = async () => {
    if (!firstName || !lastName || !email || !phone || !password || !confirmPassword) {
      Alert.alert("Error", "All fields are mandatory.");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(email)) {
      Alert.alert("Error", "Please enter a valid email.");
      return;
    }
    if (password !== confirmPassword) {
      Alert.alert("Error", "Passwords do not match.");
      return;
    }

    setLoading(true);
    try {
      // Step 1: Sign up user using Supabase's auth API
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            first_name: firstName,
            last_name: lastName,
            phone: phone,
          },
        },
      });

      if (error) throw error;

      if (data && data.user) {
        // Step 2: Insert additional profile data in `profiles` table
        const { error: profileError } = await supabase
          .from("profiles")
          .insert({
            id: data.user.id, // Link to `auth.users` table
            first_name: firstName,
            last_name: lastName,
            email: email,
            phone: phone,
            created_at: new Date(),
          });

        if (profileError) throw profileError;

        Alert.alert(
          "Success",
          "Account created successfully! Please check your email to confirm your registration."
        );

        // Navigate to login screen after successful signup
        router.push("/auth/login");
      }
    } catch (error) {
      console.error("Signup error:", error);
      Alert.alert("Error", error.message || "An unexpected error occurred");
    } finally {
      setLoading(false);
    }
  };

  return (
    <KeyboardAvoidingView
      className="flex-1 bg-[#171717]"
      behavior={Platform.OS === "ios" ? "padding" : undefined}
    >
      <Image
        className="h-[40%] w-full"
        source={{uri:"data:image/jpeg;base64,"}}
      />
      <ScrollView>
        <View className="p-4">
          <Text className="text-white text-3xl text-center mb-8">
            SIGNUP TO <Text className="text-[#F4BA45]">RANCHERS</Text>
          </Text>
          {/* First Name */}
          <Text className="text-white mb-2">First Name</Text>
          <TextInput
            className="border-2 border-[#F4BA45] rounded-lg text-white h-12 px-3 mb-5"
            placeholder="Enter your First Name"
            placeholderTextColor="#A9A9A9"
            value={firstName}
            onChangeText={setFirstName}
          />
          {/* Last Name */}
          <Text className="text-white mb-2">Last Name</Text>
          <TextInput
            className="border-2 border-[#F4BA45] rounded-lg text-white h-12 px-3 mb-5"
            placeholder="Enter your Last Name"
            placeholderTextColor="#A9A9A9"
            value={lastName}
            onChangeText={setLastName}
          />
          {/* Email */}
          <Text className="text-white mb-2">Email</Text>
          <TextInput
            className="border-2 border-[#F4BA45] rounded-lg text-white h-12 px-3 mb-5"
            placeholder="Enter your Email"
            placeholderTextColor="#A9A9A9"
            value={email}
            onChangeText={setEmail}
            keyboardType="email-address"
          />
          {/* Phone */}
          <Text className="text-white mb-2">Phone</Text>
          <TextInput
            className="border-2 border-[#F4BA45] rounded-lg text-white h-12 px-3 mb-5"
            placeholder="Enter your Phone Number"
            placeholderTextColor="#A9A9A9"
            value={phone}
            onChangeText={setPhone}
            keyboardType="phone-pad"
          />
          {/* Password */}
          <Text className="text-white mb-2">Password</Text>
          <TextInput
            className="border-2 border-[#F4BA45] rounded-lg text-white h-12 px-3 mb-5"
            placeholder="Enter your Password"
            placeholderTextColor="#A9A9A9"
            value={password}
            onChangeText={setPassword}
            secureTextEntry
          />
          {/* Confirm Password */}
          <Text className="text-white mb-2">Confirm Password</Text>
          <TextInput
            className="border-2 border-[#F4BA45] rounded-lg text-white h-12 px-3 mb-5"
            placeholder="Confirm your Password"
            placeholderTextColor="#A9A9A9"
            value={confirmPassword}
            onChangeText={setConfirmPassword}
            secureTextEntry
          />
          {/* Continue Button */}
          <TouchableOpacity
            onPress={validateSignup}
            className="bg-[#F4BA45] rounded-lg h-12 flex items-center justify-center"
            disabled={loading}
          >
            {loading ? (
              <ActivityIndicator size="small" color="#ffffff" />
            ) : (
              <Text className="text-white font-bold text-lg">CONTINUE</Text>
            )}
          </TouchableOpacity>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
}


